import { Component, Inject } from '@angular/core';
import { MAT_DIALOG_DATA, MatDialog } from '@angular/material/dialog';
import { FileHandle } from '../_model/file-handle.model';

@Component({
  selector: 'app-show-product-images-dialog',
  templateUrl: './show-product-images-dialog.component.html',
  styleUrls: ['./show-product-images-dialog.component.css'],
})
export class ShowProductImagesDialogComponent {
  constructor(@Inject(MAT_DIALOG_DATA) public data: any) {}

  receiveImages() {
    console.log(this.data);
  }
}
<mat-grid-list cols="4" rowHeight="1:1" class="m-2">
  <mat-grid-tile *ngFor="let fileHandle of data.images"
    ><img [src]="fileHandle.url" width="150px" height="150px" />
  </mat-grid-tile>
</mat-grid-list>
