import { Component } from '@angular/core';
import { ProductService } from '../_services/product.service';
import { Product } from '../_model/product.model';
import { HttpErrorResponse } from '@angular/common/http';
import { MatDialog } from '@angular/material/dialog';
import { ShowProductImagesDialogComponent } from '../show-product-images-dialog/show-product-images-dialog.component';
import { ImageProcessingService } from '../image-processing.service';
import { map } from 'rxjs';
import { Router } from '@angular/router';

@Component({
  selector: 'app-show-product-details',
  templateUrl: './show-product-details.component.html',
  styleUrls: ['./show-product-details.component.css'],
})
export class ShowProductDetailsComponent {
  pageNumber: number = 0;
  showTable: boolean = false;
  showLoadMoreProductButton = false;
  productDetails: Product[] = [];
  displayedColumns: string[] = [
    'Артикул',
    'Название',
    'description',
    'Цена без скидки',
    'Цена со скидкой',
    'Действия',
  ];

  constructor(
    private productService: ProductService,
    public imagesDialog: MatDialog,
    private imageProcessingService: ImageProcessingService,
    private router: Router
  ) {}

  ngOnInit(): void {
    this.getAllProducts();
  }

  public getAllProducts(searchKey: string = '') {
    this.showTable = false;
    this.productService
      .getAllProducts(this.pageNumber, searchKey)
      .pipe(
        map((x: Product[], i: number) =>
          x.map((product: Product) =>
            this.imageProcessingService.createImages(product)
          )
        )
      )
      .subscribe(
        (response: Product[]) => {
          console.log(response);
          //this.productDetails = response;
          response.forEach((p) => this.productDetails.push(p));
          this.showTable = true;

          if (response.length == 8) {
            this.showLoadMoreProductButton = true;
          } else {
            this.showLoadMoreProductButton = false;
          }
        },
        (error: HttpErrorResponse) => {
          console.log(error);
        }
      );
  }

  deleteProduct(productId: number) {
    this.productService.deleteProduct(productId).subscribe(
      (response: any) => {
        this.getAllProducts();
      },
      (error: HttpErrorResponse) => {
        console.log(error);
      }
    );
  }

  showImages(product: Product) {
    this.imagesDialog.open(ShowProductImagesDialogComponent, {
      data: {
        images: product.productImages,
      },
      height: '500px',
      width: '800px',
    });
  }

  editProductDetails(productId: number) {
    this.router.navigate(['/addNewProduct', { productId: productId }]);
  }

  loadMoreProduct() {
    this.pageNumber++;
    this.getAllProducts();
  }

  showAllProducts() {
    this.pageNumber = -1;
    this.productDetails = [];
    this.getAllProducts();
  }

  searchByKeyWord(searchKeyWord: string) {
    this.pageNumber = 0;
    this.productDetails = [];
    this.getAllProducts(searchKeyWord);
  }
}
<div class="mt-5 p-3">
  <mat-form-field appearance="outline" class="w-100">
    <mat-label>Поиск товаров</mat-label>
    <input
      #searchKeyWord
      (keyup)="searchByKeyWord(searchKeyWord.value)"
      matInput
      placeholder="Введите название или описание товаров"
      name="searchKey"
      id="searchKey"
    />
    <mat-hint></mat-hint>
  </mat-form-field>
  <div *ngIf="showTable">
    <table mat-table [dataSource]="productDetails" class="mat-elevation-z8">
      <ng-container matColumnDef="Артикул">
        <th mat-header-cell *matHeaderCellDef>Артикул</th>
        <td mat-cell *matCellDef="let element">{{ element.productId }}</td>
      </ng-container>

      <ng-container matColumnDef="Название">
        <th mat-header-cell *matHeaderCellDef>Название</th>
        <td mat-cell *matCellDef="let element">{{ element.productName }}</td>
      </ng-container>

      <ng-container matColumnDef="description">
        <th mat-header-cell *matHeaderCellDef>Описание</th>
        <td mat-cell *matCellDef="let element">
          {{ element.productDescription }}
        </td>
      </ng-container>

      <ng-container matColumnDef="Цена без скидки">
        <th mat-header-cell *matHeaderCellDef>Цена без скидки</th>
        <td mat-cell *matCellDef="let element">
          {{ element.productActualPrice }}
        </td>
      </ng-container>

      <ng-container matColumnDef="Цена со скидкой">
        <th mat-header-cell *matHeaderCellDef>Цена со скидкой</th>
        <td mat-cell *matCellDef="let element">
          {{ element.productDiscountedPrice }}
        </td>
      </ng-container>

      <ng-container matColumnDef="Действия">
        <th mat-header-cell *matHeaderCellDef>Действия</th>
        <td mat-cell *matCellDef="let element">
          <button mat-icon-button color="primary" (click)="showImages(element)">
            <mat-icon>images</mat-icon>
          </button>
          <button
            mat-icon-button
            color="primary"
            (click)="editProductDetails(element.productId)"
          >
            <mat-icon>edit</mat-icon>
          </button>
          <button
            mat-icon-button
            color="warn"
            (click)="deleteProduct(element.productId)"
          >
            <mat-icon>delete</mat-icon>
          </button>
        </td>
      </ng-container>

      <tr mat-header-row *matHeaderRowDef="displayedColumns"></tr>
      <tr mat-row *matRowDef="let row; columns: displayedColumns"></tr>
    </table>
  </div>
  <div class="text-center p-5" *ngIf="showLoadMoreProductButton">
    <button
      type="button"
      mat-raised-button
      color="primary"
      (click)="loadMoreProduct()"
    >
      Показать больше
    </button>
  </div>
  <div align="end" style="margin-top: -84px" *ngIf="showLoadMoreProductButton">
    <button
      type="button"
      mat-raised-button
      color="warn"
      (click)="showAllProducts()"
    >
      Показать всё (НЕ РЕКОМЕНДУЕТСЯ)
    </button>
  </div>
</div>
