import { Component, OnInit } from '@angular/core';
import { ProductService } from '../_services/product.service';
import { map } from 'rxjs';
import { Product } from '../_model/product.model';
import { ImageProcessingService } from '../image-processing.service';
import { HttpErrorResponse } from '@angular/common/http';
import { Router } from '@angular/router';

@Component({
  selector: 'app-home',
  templateUrl: './home.component.html',
  styleUrls: ['./home.component.css'],
})
export class HomeComponent implements OnInit {
  pageNumber: number = 0;
  showLoadButton = false;
  productDetails: Product[] = [];
  constructor(
    private productService: ProductService,
    private imageProcessingService: ImageProcessingService,
    private router: Router
  ) {}

  ngOnInit(): void {
    this.getAllProducts();
  }

  public getAllProducts(searchKey: string = '') {
    this.productService
      .getAllProducts(this.pageNumber, searchKey)
      .pipe(
        map((x: Product[], i: number) =>
          x.map((product: Product) =>
            this.imageProcessingService.createImages(product)
          )
        )
      )
      .subscribe(
        (response: Product[]) => {
          console.log(response);
          if (response.length === 8) {
            this.showLoadButton = true;
          } else {
            this.showLoadButton = false;
          }
          response.forEach((p) => this.productDetails.push(p));
          //this.productDetails = response;
        },
        (error: HttpErrorResponse) => {
          console.log(error);
        }
      );
  }

  showProductDetails(productId: any) {
    this.router.navigate(['/productViewDetails', { productId: productId }]);
  }

  public loadMoreProduct() {
    this.pageNumber = this.pageNumber + 1;
    this.getAllProducts();
  }

  searchByKeyWord(searchKeyWord: string) {
    this.pageNumber = 0;
    this.productDetails = [];
    this.getAllProducts(searchKeyWord);
  }
}
<div class="container mt-5">
  <mat-form-field appearance="outline" class="w-50">
    <mat-label>Поиск товаров</mat-label>
    <input
      #searchKeyWord
      (keyup)="searchByKeyWord(searchKeyWord.value)"
      matInput
      placeholder="Введите название или описание товаров"
      name="searchKey"
      id="searchKey"
    />
    <mat-hint></mat-hint>
  </mat-form-field>
  <mat-grid-list cols="4" rowHeight="2:3" gutterSize="10">
    <mat-grid-tile *ngFor="let p of productDetails">
      <div class="row p-5">
        <div class="image-container" style="height: 276px">
          <img
            class="home-page-product-image"
            [src]="p.productImages[0].url"
            alt=""
          />
        </div>

        <div class="mt-3">
          <p>
            <b>{{ p.productName }}</b>
          </p>
        </div>

        <div class="mt-2">
          <p
            style="
              white-space: inherit;
              text-overflow: ellipsis;
              height: 40px;
              width: 288px;
              overflow: hidden;
            "
          >
            {{ p.productDescription }}
          </p>
        </div>

        <div class="mt-2">
          <b>Руб. {{ p.productDiscountedPrice }}</b> &nbsp;
          <b style="text-decoration: line-through">{{
            p.productActualPrice
          }}</b>
        </div>

        <div class="mt-2">
          <button
            type="button"
            mat-raised-button
            color="warn"
            (click)="showProductDetails(p.productId)"
          >
            Подробнее...
          </button>
        </div>
      </div>
    </mat-grid-tile>
  </mat-grid-list>

  <div class="text-center mt-1 p-5">
    <button
      type="button"
      mat-raised-button
      color="primary"
      (click)="loadMoreProduct()"
      *ngIf="showLoadButton"
    >
      Показать больше
    </button>
  </div>
</div>
